import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    axios.get("http://localhost:5000/api/products").then(res => setProducts(res.data));
    fetchCart();
  }, []);

  const fetchCart = () => {
    axios.get("http://localhost:5000/api/cart").then(res => {
      setCart(res.data.cart);
      setTotal(res.data.total);
    });
  };

  const addToCart = (id) => {
    axios.post("http://localhost:5000/api/cart", { productId: id, qty: 1 }).then(() => fetchCart());
  };

  const removeItem = (id) => {
    axios.delete(`http://localhost:5000/api/cart/${id}`).then(() => fetchCart());
  };

  const checkout = () => {
    axios.post("http://localhost:5000/api/checkout", { cartItems: cart }).then(res => {
      alert(`‚úÖ Checkout successful! Total: $${res.data.total}`);
      fetchCart();
    });
  };

  return (
    <div className="p-4">
      <h1>üõí Vibe Commerce</h1>

      <h2>Products</h2>
      <div style={{ display: "flex", gap: "1rem", flexWrap: "wrap" }}>
        {products.map(p => (
          <div key={p.id} style={{ border: "1px solid #ccc", padding: "10px" }}>
            <p>{p.name}</p>
            <p>${p.price}</p>
            <button onClick={() => addToCart(p.id)}>Add to Cart</button>
          </div>
        ))}
      </div>

      <h2>Cart</h2>
      {cart.map(item => (
        <div key={item.productId}>
          {item.name} x {item.qty} = ${item.price * item.qty}
          <button onClick={() => removeItem(item.productId)}>‚ùå</button>
        </div>
      ))}
      <h3>Total: ${total}</h3>
      <button onClick={checkout}>Checkout</button>
    </div>
  );
}

export default App;
