import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 6;

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then(res => {
        setProducts(res.data);
        setLoading(false);
      })
      .catch(err => console.log(err));
    fetchCart();
  }, []);

  const fetchCart = () => {
    axios.get("http://localhost:5000/api/cart").then(res => {
      setCart(res.data.cart);
      setTotal(res.data.total);
    });
  };

  const addToCart = (p) => {
    axios.post("http://localhost:5000/api/cart", {
      productId: p.id,
      qty: 1,
      name: p.title,
      price: p.price
    }).then(() => fetchCart());
  };

  const removeItem = (id) => {
    axios.delete(`http://localhost:5000/api/cart/${id}`).then(() => fetchCart());
  };

  const checkout = () => {
    axios.post("http://localhost:5000/api/checkout", { cartItems: cart })
      .then(res => {
        alert(`✅ Checkout successful!\nTotal: $${res.data.total}`);
        fetchCart();
      });
  };

  // Pagination logic
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentProducts = products.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(products.length / itemsPerPage);

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>🛍️ Vibe Commerce</h1>

      <div style={styles.main}>
        {/* Products */}
        <div style={styles.productsSection}>
          <h2 style={styles.subtitle}>Products</h2>
          {loading ? (
            <p>Loading products...</p>
          ) : (
            <>
              <div style={styles.grid}>
                {currentProducts.map(p => (
                  <div key={p.id} style={styles.card}>
                    <img src={p.image} alt={p.title} style={styles.image} />
                    <h3 style={styles.name}>
                      {p.title.length > 40 ? p.title.slice(0, 40) + "..." : p.title}
                    </h3>
                    <p style={styles.price}>${p.price}</p>
                    <button style={styles.button} onClick={() => addToCart(p)}>
                      Add to Cart
                    </button>
                  </div>
                ))}
              </div>

              {/* Pagination */}
              <div style={styles.pagination}>
                <button
                  style={styles.pageBtn}
                  onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                  disabled={currentPage === 1}
                >
                  ⬅ Prev
                </button>
                <span style={styles.pageText}>Page {currentPage} of {totalPages}</span>
                <button
                  style={styles.pageBtn}
                  onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                  disabled={currentPage === totalPages}
                >
                  Next ➡
                </button>
              </div>
            </>
          )}
        </div>

        {/* Cart */}
        <div style={styles.cartSection}>
          <h2 style={styles.subtitle}>🛒 Your Cart</h2>
          {cart.length === 0 ? (
            <p style={styles.emptyCart}>Your cart is empty.</p>
          ) : (
            <div>
              {cart.map(item => (
                <div key={item.productId} style={styles.cartItem}>
                  <div style={styles.cartDetails}>
                    <p style={styles.cartName}>
                      {item.name.length > 45 ? item.name.slice(0, 45) + "..." : item.name}
                    </p>
                    <p style={styles.cartMeta}>Qty: {item.qty} × ${item.price}</p>
                  </div>
                  <div style={styles.cartRight}>
                    <p style={styles.cartPrice}>${(item.price * item.qty).toFixed(2)}</p>
                    <button
                      style={styles.removeBtn}
                      onClick={() => removeItem(item.productId)}
                      title="Remove item"
                    >
                      ✖
                    </button>
                  </div>
                </div>
              ))}

              <div style={styles.cartFooter}>
                <h3 style={styles.total}>Total: ${total.toFixed(2)}</h3>
                <button style={styles.checkoutBtn} onClick={checkout}>
                  ✅ Checkout
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

// 🎨 Styles
const styles = {
  container: {
    fontFamily: "'Inter', sans-serif",
    padding: "30px",
    maxWidth: "1200px",
    margin: "0 auto",
  },
  title: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
  },
  main: {
    display: "flex",
    gap: "40px",
    flexWrap: "wrap",
  },
  productsSection: {
    flex: "2",
    minWidth: "600px",
  },
  cartSection: {
    flex: "1",
    minWidth: "320px",
    background: "#ffffff",
    padding: "20px",
    borderRadius: "12px",
    boxShadow: "0 4px 10px rgba(0,0,0,0.08)",
    height: "fit-content",
    border: "1px solid #eee",
  },
  subtitle: {
    fontSize: "1.3rem",
    margin: "10px 0",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(180px, 1fr))",
    gap: "20px",
  },
  card: {
    border: "1px solid #ddd",
    borderRadius: "10px",
    padding: "15px",
    textAlign: "center",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
  },
  image: {
    width: "100%",
    height: "160px",
    objectFit: "contain",
  },
  name: {
    fontSize: "0.9rem",
    margin: "8px 0",
    color: "#333",
  },
  price: {
    fontWeight: "bold",
    color: "#007b5e",
  },
  button: {
    background: "#007b5e",
    color: "white",
    padding: "6px 10px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    marginTop: "5px",
  },
  // 🛒 Cart styles
  emptyCart: {
    color: "#777",
    textAlign: "center",
    marginTop: "20px",
  },
  cartItem: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "10px 0",
    borderBottom: "1px solid #eee",
  },
  cartDetails: {
    flex: 1,
    paddingRight: "10px",
  },
  cartName: {
    fontWeight: "500",
    fontSize: "0.95rem",
    color: "#222",
  },
  cartMeta: {
    fontSize: "0.85rem",
    color: "#555",
  },
  cartRight: {
    display: "flex",
    alignItems: "center",
    gap: "10px",
  },
  cartPrice: {
    fontWeight: "bold",
    color: "#007b5e",
  },
  removeBtn: {
    background: "#dc3545",
    color: "white",
    border: "none",
    borderRadius: "50%",
    width: "28px",
    height: "28px",
    fontSize: "14px",
    lineHeight: "28px",
    textAlign: "center",
    cursor: "pointer",
  },
  cartFooter: {
    marginTop: "15px",
    textAlign: "center",
  },
  total: {
    fontSize: "1.2rem",
    marginBottom: "10px",
    fontWeight: "600",
  },
  checkoutBtn: {
    background: "#28a745",
    color: "white",
    border: "none",
    borderRadius: "8px",
    padding: "10px 15px",
    fontSize: "1rem",
    cursor: "pointer",
    width: "100%",
    transition: "background 0.2s ease",
  },
  pagination: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    gap: "15px",
    marginTop: "20px",
  },
  pageBtn: {
    background: "#007b5e",
    color: "white",
    border: "none",
    borderRadius: "5px",
    padding: "6px 10px",
    cursor: "pointer",
  },
  pageText: {
    fontWeight: "bold",
  },
};

export default App;
