import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 6;

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then(res => {
        setProducts(res.data);
        setLoading(false);
      })
      .catch(err => console.log(err));
    fetchCart();
  }, []);

  const fetchCart = () => {
    axios.get("http://localhost:5000/api/cart").then(res => {
      setCart(res.data.cart);
      setTotal(res.data.total);
    });
  };

  const addToCart = (p) => {
    axios.post("http://localhost:5000/api/cart", {
      productId: p.id,
      qty: 1,
      name: p.title,
      price: p.price
    }).then(() => fetchCart());
  };

  const removeItem = (id) => {
    axios.delete(`http://localhost:5000/api/cart/${id}`).then(() => fetchCart());
  };

  const checkout = () => {
    axios.post("http://localhost:5000/api/checkout", { cartItems: cart })
      .then(res => {
        alert(`✅ Checkout successful!\nTotal: $${res.data.total}`);
        fetchCart();
      });
  };

  // Pagination logic
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentProducts = products.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(products.length / itemsPerPage);

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>🛍️ Vibe Commerce</h1>

      <div style={styles.main}>
        {/* Left: Product Grid */}
        <div style={styles.productsSection}>
          <h2 style={styles.subtitle}>Products</h2>
          {loading ? (
            <p>Loading products...</p>
          ) : (
            <>
              <div style={styles.grid}>
                {currentProducts.map(p => (
                  <div key={p.id} style={styles.card}>
                    <img src={p.image} alt={p.title} style={styles.image} />
                    <h3 style={styles.name}>
                      {p.title.length > 40 ? p.title.slice(0, 40) + "..." : p.title}
                    </h3>
                    <p style={styles.price}>${p.price}</p>
                    <button style={styles.button} onClick={() => addToCart(p)}>
                      Add to Cart
                    </button>
                  </div>
                ))}
              </div>

              {/* Pagination Buttons */}
              <div style={styles.pagination}>
                <button
                  style={styles.pageBtn}
                  onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                  disabled={currentPage === 1}
                >
                  ⬅ Prev
                </button>
                <span style={styles.pageText}>Page {currentPage} of {totalPages}</span>
                <button
                  style={styles.pageBtn}
                  onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                  disabled={currentPage === totalPages}
                >
                  Next ➡
                </button>
              </div>
            </>
          )}
        </div>

        {/* Right: Cart */}
        <div style={styles.cartSection}>
          <h2 style={styles.subtitle}>Cart</h2>
          {cart.length === 0 ? (
            <p>Your cart is empty 🛒</p>
          ) : (
            <div>
              {cart.map(item => (
                <div key={item.productId} style={styles.cartItem}>
                  <span>{item.name}</span>
                  <span>x {item.qty}</span>
                  <span>${(item.price * item.qty).toFixed(2)}</span>
                  <button style={styles.removeBtn} onClick={() => removeItem(item.productId)}>
                    ✖
                  </button>
                </div>
              ))}
              <h3 style={styles.total}>Total: ${total.toFixed(2)}</h3>
              <button style={styles.checkoutBtn} onClick={checkout}>Checkout</button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

const styles = {
  container: {
    fontFamily: "'Inter', sans-serif",
    padding: "30px",
    maxWidth: "1200px",
    margin: "0 auto",
  },
  title: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
  },
  main: {
    display: "flex",
    gap: "40px",
    flexWrap: "wrap",
  },
  productsSection: {
    flex: "2",
    minWidth: "600px",
  },
  cartSection: {
    flex: "1",
    minWidth: "300px",
    background: "#f9f9f9",
    padding: "20px",
    borderRadius: "12px",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
    height: "fit-content",
  },
  subtitle: {
    fontSize: "1.3rem",
    margin: "10px 0",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(180px, 1fr))",
    gap: "20px",
  },
  card: {
    border: "1px solid #ddd",
    borderRadius: "10px",
    padding: "15px",
    textAlign: "center",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
    transition: "transform 0.2s ease",
  },
  image: {
    width: "100%",
    height: "160px",
    objectFit: "contain",
  },
  name: {
    fontSize: "0.9rem",
    margin: "8px 0",
    color: "#333",
  },
  price: {
    fontWeight: "bold",
    color: "#007b5e",
  },
  button: {
    background: "#007b5e",
    color: "white",
    padding: "6px 10px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    marginTop: "5px",
  },
  cartItem: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: "10px",
    fontSize: "0.9rem",
  },
  removeBtn: {
    background: "#dc3545",
    color: "white",
    border: "none",
    borderRadius: "5px",
    padding: "4px 8px",
    cursor: "pointer",
  },
  total: {
    fontSize: "1.1rem",
    marginTop: "10px",
    borderTop: "1px solid #ccc",
    paddingTop: "10px",
  },
  checkoutBtn: {
    background: "#28a745",
    color: "white",
    border: "none",
    borderRadius: "8px",
    padding: "8px 12px",
    fontSize: "1rem",
    cursor: "pointer",
    marginTop: "10px",
    width: "100%",
  },
  pagination: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    gap: "15px",
    marginTop: "20px",
  },
  pageBtn: {
    background: "#007b5e",
    color: "white",
    border: "none",
    borderRadius: "5px",
    padding: "6px 10px",
    cursor: "pointer",
  },
  pageText: {
    fontWeight: "bold",
  },
};

export default App;
